con:testCase.con:setupScript:
import com.eviware.soapui.impl.wsdl.teststeps.*
import com.eviware.soapui.support.types.*;

StringToStringMap headersForUser = new StringToStringMap();
StringToStringMap headersForAdmin = new StringToStringMap();
StringToStringMap headersForSuper = new StringToStringMap();
StringToStringMap headersForKeycloak = new StringToStringMap();

String superUserPass = testCase.testSuite.project.getPropertyValue("meveoSuperUsername")+":"+testCase.testSuite.project.getPropertyValue("meveoSuperPassword");
 log.info("superUserPass="+superUserPass);

String adminPass = testCase.testSuite.project.getPropertyValue("meveoAdminUsername")+":"+testCase.testSuite.project.getPropertyValue("meveoAdminPassword");
 log.info("adminPass="+adminPass);

String userPass = testCase.testSuite.project.getPropertyValue("meveoUserUsername")+":"+testCase.testSuite.project.getPropertyValue("meveoUserPassword");
 log.info("userPass="+userPass);

 String keycloakPass = testCase.testSuite.project.getPropertyValue("keycloakUsername")+":"+testCase.testSuite.project.getPropertyValue("keycloakPassword");
 log.info("keycloakPass="+keycloakPass);

headersForUser.put("Authorization","Basic "+userPass.getBytes().encodeBase64());
headersForAdmin.put("Authorization","Basic "+adminPass.getBytes().encodeBase64());
headersForSuper.put("Authorization","Basic "+superUserPass.getBytes().encodeBase64());
headersForKeycloak.put("Authorization","Basic "+keycloakPass.getBytes().encodeBase64());

for(testSuiteItem in  testCase.testSuite.project.getTestSuiteList()){
	for( testCaseItem in testSuiteItem.getTestCaseList() ) {
	    log.info("Setting basic auth for all WSDL test requests in test case ["+testCaseItem.getName()+"]")
	    for( testStep in testCaseItem.getTestStepList() ) {

	          if (!(testStep instanceof WsdlTestRequestStep || testStep instanceof RestTestRequestStep || testStep instanceof HttpTestRequestStep )){
	          	continue;
	          }

	    		testStep.getTestRequest().setPreemptive(true);


			if(testStep.getName().contains("AUTHORIZATION_MANUAL")) {
				testStep.getTestRequest().setRequestHeaders(new StringToStringMap());

		    	} else if (testStep.getName().contains("AUTHORIZATION_USER")) {
		    		testStep.getTestRequest().setRequestHeaders(headersForUser);

		    	} else if (testStep.getName().contains("AUTHORIZATION_ADMIN")) {
		    		testStep.getTestRequest().setRequestHeaders(headersForAdmin);

		    	} else if(testCaseItem.getName().contains("AUTHORIZATION_SUPERADMIN") 
		    	    || testStep.getName().contains("AUTHORIZATION_SUPERADMIN")){
		    		testStep.getTestRequest().setRequestHeaders(headersForSuper);

		    	} else if (testStep.getName().contains("BEARER_TOKEN")) {

		    	} else if (testStep.getName().contains("KEYCLOAK_USER")) {
		    		testStep.getTestRequest().setRequestHeaders(headersForKeycloak);

		    	} else if(!testStep.getName().contains("AUTHORIZATION_MANUAL")) {
				testStep.getTestRequest().setRequestHeaders(headersForAdmin);
		    	}
	    }
	}
}
